webpackJsonp([0xdd78507cfee8],{405:function(a,n){a.exports={data:{site:{siteMetadata:{title:"Sanjeev Kumar Pandit",subtitle:"#FullStackWebDeveloper",copyright:"Â© All rights reserved.",author:{name:"Sanjeev Kumar Pandit",twitter:"sanjeevkpandit"},disqusShortname:"sanjeevkpanditcomnp",url:"http://sanjeevkpandit.com.np"}},markdownRemark:{id:"/run/media/sanjeev/Code/Projects/Personal/sanjeevkpandit.github.io/src/pages/articles/2018-04-16---Arch-Linux-post-installation/index.md absPath of file >>> MarkdownRemark",html:'<h3>Update your system</h3>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -Syu</code></pre>\n      </div>\n<h3>Install X server, Desktop Environment and Display Manager</h3>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S xorg</code></pre>\n      </div>\n<p>To install GNOME:</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S gnome gnome-extra</code></pre>\n      </div>\n<p>To install Cinnamon:</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S cinnamon nemo-file-roller</code></pre>\n      </div>\n<p>To install XFCE:</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S xfce4 xfce4-goodies</code></pre>\n      </div>\n<p>To install KDE:</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S plasma</code></pre>\n      </div>\n<p>To install MATE:</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S mate mate-extra</code></pre>\n      </div>\n<p>To install a display manager:</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S lightdm\n# systemctl enable lightdm.service</code></pre>\n      </div>\n<h3>Add user</h3>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console">[Add new user]\n# useradd -m -G wheel -s /bin/bash username\n# passwd username\n\n[Uncomment wheel group]\n# visudo\n%wheel ALL=(ALL) ALL\n\n[Install sudo package]\n# pacman -S sudo</code></pre>\n      </div>\n<h3>Install yaourt</h3>\n<p>Add followings to <code class="language-text">/etc/pacman.conf</code>:</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console">[archlinuxfr]\nSigLevel = Never\nServer = http://repo.archlinux.fr/$arch</code></pre>\n      </div>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -Syu yaourt</code></pre>\n      </div>\n<h3>Install applications</h3>\n<ul>\n<li>\n<p>Internet</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S firefox\n# pacman -S chromium\n# pacman -S opera</code></pre>\n      </div>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console">$ yaourt -S google-chrome\n$ yaourt -S skypeforlinux-stable-bin</code></pre>\n      </div>\n</li>\n<li>\n<p>Fonts</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S ttf-hack\n# pacman -S noto-fonts\n# pacman -S ttf-symbola\n# pacman -S ttf-indic-otf\n# pacman -S noto-fonts-extra\n# pacman -S noto-fonts-emoji\n# pacman -S adobe-source-code-pro-fonts</code></pre>\n      </div>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console">$ yaourt -S ttf-monaco</code></pre>\n      </div>\n</li>\n<li>\n<p>Text Editors</p>\n<p>Install Sublime Text from <a href="https://www.sublimetext.com/docs/3/linux_repositories.html#pacman" target="_blank">here</a>.</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S atom</code></pre>\n      </div>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console">$ yaourt -S visual-studio-code-bin\n$ yaourt -S webstorm\n$ yaourt -S datagrip</code></pre>\n      </div>\n</li>\n<li>\n<p>Media Players</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S mpv\n# pacman -S vlc</code></pre>\n      </div>\n</li>\n<li>\n<p>Utilities</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S redshift\n# pacman -S htop\n# pacman -S powertop\n# pacman -S gparted</code></pre>\n      </div>\n</li>\n<li>\n<p>Power management</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S tlp\n# systemctl enable tlp.service</code></pre>\n      </div>\n</li>\n<li>\n<p>Download manager</p>\n<div class="gatsby-highlight">\n      <pre class="language-console"><code class="language-console"># pacman -S aria2\n# pacman -S qbittorent</code></pre>\n      </div>\n</li>\n</ul>\n<h3>Reboot and enjoy!</h3>\n<h4>Reference:</h4>\n<ul>\n<li><a href="https://itsfoss.com/things-to-do-after-installing-arch-linux/" target="_blank">7 Essential Things To Do After Installing Arch Linux</a></li>\n<li><a href="https://www.2daygeek.com/arch-linux-post-installation-30-things-to-do-after-installing-arch-linux/2/" target="_blank">Arch Linux Post Installation (30 Things to do after Installing Arch Linux)</a></li>\n</ul>',fields:{tagSlugs:["/tags/arch/","/tags/arch-linux/","/tags/linux/","/tags/post-installation/"]},frontmatter:{title:"Arch Linux post installation",tags:["arch","arch linux","linux","post installation"],date:"2018-04-16T00:00:00Z",description:"Post installation steps for arch linux users."}}},pathContext:{slug:"/posts/arch-linux-post-installation/"}}}});
